// Gallery.jsx
import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import Image from 'next/image';

gsap.registerPlugin(ScrollTrigger);

const photos = [
  { src: 'data:image/jpeg;base64,', alt: 'Description 1' },
  { src: 'data:image/jpeg;base64,', alt: 'Description 2' },
  { src: 'data:image/jpeg;base64,', alt: 'Description 3' },
  { src: 'data:image/jpeg;base64,', alt: 'Description 4' },
];

const Gallery = () => {
  const galleryRef = useRef(null);

  useEffect(() => {
    const elements = galleryRef.current.children;

    gsap.set(elements, { opacity: 0 });

    gsap.to(elements, {
      opacity: 1,
      duration: 0.5,
      stagger: 0.2,
      scrollTrigger: {
        trigger: galleryRef.current,
        start: 'top 80%',
        end: 'bottom 20%',
        toggleActions: 'play reset play reset',
      },
    });

    Array.from(elements).forEach((el) => {
      const overlay = el.querySelector('.overlay');
      const image = el.querySelector('img');

      gsap.set(overlay, { backgroundColor: '#3490dc', width: '100%', height: '100%' });
      gsap.set(image, { opacity: 0 });

      gsap.timeline({
        scrollTrigger: {
          trigger: el,
          start: 'top 90%',
          end: 'bottom 10%',
          toggleActions: 'play none none none',
        },
      })
        .to(overlay, { width: 0, duration: 1 })
        .to(image, { opacity: 1, duration: 0.5 }, '-=0.5');
    });
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Photography Gallery</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4" ref={galleryRef}>
        {photos.map((photo, index) => (
          <div key={index} className="relative overflow-hidden rounded-lg shadow-lg">
            <div className="overlay absolute inset-0 z-10"></div>
            <Image src={photo.src} alt={photo.alt} width={100} height={100} className="w-full h-full object-cover" />
          </div>
        ))}
      </div>
      <div className="mt-4 text-center">
        <a
          href="https://www.instagram.com/your-instagram"
          target="_blank"
          rel="noopener noreferrer"
          className="inline-block px-6 py-2 text-white bg-blue-500 hover:bg-blue-600 rounded-full transition-colors duration-300"
        >
          See More on Instagram
        </a>
      </div>
    </div>
  );
};

export default Gallery;
